import "./ExpenseItem";
import ExpenseItem from "./ExpenseItem";
import "./MyExpenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";

const MyExpenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2021");
  const ChangeHandler = (SelectedYear) => {
    setFilteredYear(SelectedYear);
  };

  const filteredExpense = props.data.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  let expense

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter myYear={filteredYear} onChaneFilter={ChangeHandler} />
        {filteredExpense.length === 0 && <p>No expense found.</p>}
        {filteredExpense.length > 0 &&
          filteredExpense.map((filterExpense) => (
            <ExpenseItem
              key={filterExpense.id}
              title={filterExpense.title}
              amount={filterExpense.amount}
              date={filterExpense.date}
            />
          ))}
      </Card>
    </div>
  );
};

export default MyExpenses;
